import React, { Component } from 'react';
import { GoogleApiWrapper } from 'google-maps-react';

class NearbyRestaurants extends Component {
  state = {
    bestRestaurants: []
  };

  componentDidMount() {
    // Get user's current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.searchNearbyRestaurants);
    } else {
      console.log("Geolocation is not supported by this browser.");
    }
  }

  searchNearbyRestaurants = (position) => {
    const { google } = this.props;
    const { latitude, longitude } = position.coords;

    // Create a PlacesService instance
    const placesService = new google.maps.places.PlacesService(document.createElement('div'));

    // Define search parameters
    const request = {
      location: new google.maps.LatLng(latitude, longitude),
      radius: 500, // Search radius in meters (adjust as needed)
      type: 'restaurant',
      rankBy: google.maps.places.RankBy.PROMINENCE // Sort by prominence
    };

    // Perform nearby search
    placesService.nearbySearch(request, (results, status) => {
      if (status === google.maps.places.PlacesServiceStatus.OK) {
        // Sort the restaurants based on rating (you can use your own ranking algorithm)
        const sortedRestaurants = results.sort((a, b) => b.rating - a.rating).slice(0, 5); // Select top 5 restaurants

        // Update state with the best 5 restaurants
        this.setState({ bestRestaurants: sortedRestaurants });
      } else {
        console.error('Error fetching nearby restaurants:', status);
      }
    });
  }

  // Function to categorize restaurant into its cuisine
  getCuisineCategory = (restaurantName) => {
    // Implement your logic to categorize each restaurant into its cuisine category
    // For simplicity, let's assume the cuisine categories based on the restaurant name
    // You can replace this with a more sophisticated logic if needed
    const cuisineMap = {
      '신수돈가스': 'Kooorean',
      'Chez Pierre': 'French',
      'Sakura Sushi Bar': 'Japanese',
      'Taj Mahal Indian Cuisine': 'Indian',
      'Spice Garden Thai Restaurant': 'Thai'
    };

    return cuisineMap[restaurantName] || 'Unknown'; // Default to 'Unknown' if cuisine not found
  }

  render() {
    const { bestRestaurants } = this.state;

    return (
      <div>
        <h2>Top 5 Nearby Restaurants by Cuisine</h2>
        <ul>
          {bestRestaurants.map((restaurant, index) => (
            <li key={index}>
              {restaurant.name} - {this.getCuisineCategory(restaurant.name)}
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyCnv1eojLjZwtHpBXy_QZ3bSv2slXEsLyc'
})(NearbyRestaurants);
