import React, { Component } from 'react';
import { Map, GoogleApiWrapper } from 'google-maps-react';
import './style.css';


class NearbyRestaurants extends Component {
  state = {
    bestRestaurants: [],
    nextPageToken: null, // Initialize nextPageToken state
    showMapModal: false, // State to control visibility of the map modal
    currentLocation: null // Store current location
  };

  // Function to toggle the visibility of the map modal
  toggleMapModal = () => {
    this.setState(prevState => ({
      showMapModal: true
    }));
  };

  // Function to handle location selection from the map
  handleLocationSelection = (mapProps, map) => {
    const { google } = this.props;
    const latLng = map.getCenter(); // Get the center coordinates of the map

    // Set the current location in the state
    this.setState({ currentLocation: latLng });

    // Close the map modal
    this.toggleMapModal();

    // Perform a new nearby search for restaurants in the selected location
    // You can use the selectedLocation coordinates to perform the search
    // For brevity, I'm skipping the implementation of the nearby search in this example
  };

  componentDidMount() {
    // Get user's current location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.searchNearbyRestaurants);
    } else {
      console.log("Geolocation is not supported by this browser.");
    }
  }

  searchNearbyRestaurants = (position) => {
    const { google } = this.props;
    const { latitude, longitude } = position.coords;

    // Create a PlacesService instance
    const placesService = new google.maps.places.PlacesService(document.createElement('div'));

    // Define search parameters
    const request = {
      location: new google.maps.LatLng(latitude, longitude),
      radius: 5000, // Search radius in meters (adjust as needed)
      type: 'restaurant',
      rankBy: google.maps.places.RankBy.PROMINENCE, // Sort by prominence
      types: ['restaurant'] 
    };

    // Perform nearby search
    placesService.nearbySearch(request, (results, status, pagination) => {
      if (status === google.maps.places.PlacesServiceStatus.OK) {
        // Filter restaurants with 4+ stars and 50+ reviews
        const filteredRestaurants = results.filter(restaurant => {
          return restaurant.rating >= 4.2 && restaurant.user_ratings_total >= 30 &&
           !restaurant.name.toLowerCase().includes('hotel');
        });

        // Fetch category for each restaurant
        const promises = filteredRestaurants.map(restaurant => {
          return new Promise((resolve, reject) => {
            // Get details for each restaurant
            const detailsRequest = {
              placeId: restaurant.place_id,
              fields: ['name', 'types'] // Retrieve name and types (categories)
            };
            placesService.getDetails(detailsRequest, (placeDetails, status) => {
              if (status === google.maps.places.PlacesServiceStatus.OK) {
                const category = placeDetails.types && placeDetails.types.length > 0 ? placeDetails.types[0] : 'Uncategorized';
                resolve({ ...restaurant, category });
              } else {
                console.error('Error fetching restaurant details:', status);
                reject(status);
              }
            });
          });
        });

        // Resolve all promises
        Promise.all(promises)
          .then(restaurantsWithCategory => {
            // Update state with the filtered restaurants, category, and nextPageToken
            this.setState({ bestRestaurants: restaurantsWithCategory.slice(0, 1), nextPageToken: pagination.hasNextPage ? pagination.nextPageToken : null });
          })
          .catch(error => {
            console.error('Error fetching restaurant details:', error);
          });
      } else {
        console.error('Error fetching nearby restaurants:', status);
      }
    });
  }

  loadNextRestaurants = () => {
    const { google } = this.props;
    const { nextPageToken } = this.state;

    // If nextPageToken is available, fetch the next page of restaurants
    if (nextPageToken) {
      const placesService = new google.maps.places.PlacesService(document.createElement('div'));

      // Define search parameters for the next page
      const request = {
        pagetoken: nextPageToken
      };

      // Perform nearby search for the next page
      placesService.nearbySearch(request, (results, status, pagination) => {
        if (status === google.maps.places.PlacesServiceStatus.OK) {
          // Filter restaurants with 4+ stars and 50+ reviews
          const filteredRestaurants = results.filter(restaurant => {
            return restaurant.rating >= 4 && restaurant.user_ratings_total >= 30;
          });

          // Fetch category for each restaurant
          const promises = filteredRestaurants.map(restaurant => {
            return new Promise((resolve, reject) => {
              // Get details for each restaurant
              const detailsRequest = {
                placeId: restaurant.place_id,
                fields: ['name', 'types'] // Retrieve name and types (categories)
              };
              placesService.getDetails(detailsRequest, (placeDetails, status) => {
                if (status === google.maps.places.PlacesServiceStatus.OK) {
                  const category = placeDetails.types && placeDetails.types.length > 0 ? placeDetails.types[0] : 'Uncategorized';
                  resolve({ ...restaurant, category });
                } else {
                  console.error('Error fetching restaurant details:', status);
                  reject(status);
                }
              });
            });
          });

          // Resolve all promises
          Promise.all(promises)
            .then(restaurantsWithCategory => {
              // Update state with the next page of restaurants, category, and nextPageToken
              this.setState(prevState => ({
                bestRestaurants: [...prevState.bestRestaurants, ...restaurantsWithCategory],
                nextPageToken: pagination.hasNextPage ? pagination.nextPageToken : null
              }));
            })
            .catch(error => {
              console.error('Error fetching restaurant details:', error);
            });
        } else {
          console.error('Error fetching nearby restaurants:', status);
        }
      });
    }
  }

  render() {
    const { bestRestaurants, showMapModal, currentLocation } = this.state;


    return (
      <div>
        <h2 style={{ textAlign: 'center' }}>이거나 머거랑</h2>
        <button onClick={this.toggleMapModal} className={this.state.showMapModal ? 'button-active' : 'button-default'}>
        Locate Yourself in a Map
        </button>
        {/* Map modal */}
        {/* Show map modal status */}
        <p>{showMapModal ? 'Map modal is open' : 'Map modal is closed'}</p>
        {showMapModal && (
          <div className="map-modal">
            <Map
              google={this.props.google}
              onReady={this.handleLocationSelection}
              centerAroundCurrentLocation
              zoom={15}
            />
          </div>
        )}

        <div style={{ display: 'flex', flexWrap: 'wrap'}}>
          {bestRestaurants.map((restaurant, index) => (
            <div key={index} style={{ margin: '10px', textAlign: 'center', width: '200px' }}>
              <div>
                {restaurant.photos && restaurant.photos.length > 0 && (
                  <>
                    <a href={`https://www.google.com/maps/search/?api=1&query=${restaurant.name}&query_place_id=${restaurant.place_id}`} target="_blank" rel="noopener noreferrer">
                      <img src={restaurant.photos[0].getUrl()} alt={restaurant.name} style={{ width: '200px', height: '150px', marginBottom: '5px' }} />
                    </a>
                  </>
                )}
              </div>
              <p style={{ fontWeight: 'bold', marginTop: '0' }}>{restaurant.name}</p>
              <p style={{ marginBottom: '5px' }}>{restaurant.category}</p>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <p style={{ marginRight: '5px' }}>{restaurant.rating} stars</p>
                <p>({restaurant.user_ratings_total} reviews)</p>
              </div>
            </div>
          ))}
        </div>
        <div style={{ display: 'flex', justifyContent: 'center' }}>
          <button onClick={this.loadNextRestaurants} style={{ marginTop: '20px', padding: '10px', backgroundColor: '#ff5722', color: 'black', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
            Hit me up for more restaurants!
          </button>
        </div>
      </div>
    );
  }
}


export default GoogleApiWrapper({
  apiKey: 'AIzaSyCnv1eojLjZwtHpBXy_QZ3bSv2slXEsLyc'
})(NearbyRestaurants);
